import AcUnitIcon from '@mui/icons-material/AcUnit';
import DeviceThermostatIcon from '@mui/icons-material/DeviceThermostat';
import ThunderstormIcon from '@mui/icons-material/Thunderstorm';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import './InfoBox.css';
export default function InfoBox({info}){
    // let info={
    //     city:"",
    //     temp_c:"",
    //     humidity:"",
    //     feelLike:"",
    //     conditon:""
    // }
    const HOT_URL='data:image/png;base64,';
    const COLD_URL='data:image/png;base64,';
    const RAIN_URL='data:image/png;base64,';
    return(
        <div className='infoBox'>
            <h3>Weather Info</h3>

            <hr></hr>
            <div className='card'>
                <Card sx={{ width: '100%', maxWidth: 800 }}>
                    <div style={{ padding: '16px' }}>
                        <CardMedia
                            sx={{ height: 200 ,borderRadius: '8px'}}
                            image={info.humidity>50 ? RAIN_URL  : (info.temp_c>15 ? HOT_URL :COLD_URL )}
                            title="weather"
                        />
                    </div>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                        {info.city}
                        {info.humidity>50 ? <ThunderstormIcon/>  : (info.temp_c>15 ? <DeviceThermostatIcon/> :<AcUnitIcon/> )}
                        
                        </Typography>
                        <Typography variant="body2" sx={{ color: 'text.secondary' }}>
                            <span>Temperature:{info.temp_c}&#176;C</span><br></br>
                            <span>Humidity:{info.humidity}</span><br></br>
                            <span>Feels Like:{info.feelLike}&#176;C</span><br></br>
                            <span>Weather:{info.weather}</span><br></br>
                        </Typography>
                    </CardContent>
                </Card>
            </div>
        </div>
    );
}


